package javapractice;

public class sports {

	public static void main(pm p) {
		// TODO Auto-generated method stub
		switch(p){
		case PINGPONG:
			System.out.println("乒乓");
			break;
		case BASKETBALL:
			System.out.println("篮球");
			break;
		case FOOTBALL:
			System.out.println("足球");
			break;
			default:
				System.out.println("错误");
		}

	}

}
